="' . $secQ . '" WHERE activation="' . $link . '"';		$query_2 = 'UPDATE user SET sec_a="' . $secA . '" WHERE activation="' . $link . '"';		$query_3 = 'UPDATE user SET confirmed="Y" WHERE activation="' . $link . '"';				$result1 = mysqli_query($con, $query_1);		$result2 = mysqli_query($con, $query_2);		$result3 = mysqli_query($con, $query_3);				echo '1';	}	else if($type === 'verifyAutorization'){		$link = $_POST['link'];		$oib = $_POST['oib'];		$secKey = $_POST['secKey'];		$tmp_pass = 0;		$query = 'SELECT tmp_pass FROM user WHERE activation="'.$link.'" AND id='.$oib.' AND activation_key='.$secKey.' LIMIT 0, 1';		$result = mysqli_query($con, $query);		if(!result) return false;		while($row = mysqli_fetch_array($result)){					$tmp_pass = $row['tmp_pass'];		}		echo $tmp_pass;	}	else if($type === 'unReg'){		$reg = $fgmembersite->unReg($_POST['search']);		echo $reg;	}	else if($type === 'autoComplete'){		$reg = $fgmembersite->unReg_Auto($_POST['id']);		echo $reg;	}	else if($type === 'getLocations'){		$reg = $fgmembersite->getLocations($_POST['id']);		echo $reg;	}	} else { ?>	<!DOCTYPE HTML>	<html lang="en-US">	<head>		<meta charset="UTF-8">		<title>Stranica je nedostupna</title>	</head>	<body>		<h2>			test		</h2>	</body>	</html><?php }function sendmail($email, $type, $key, $link, $pass, $uid){	if($type=='activation'){				$from = 'info@enzo.hr';		$subject = 'Aktivacije Enzo.hr računa';		$message = '			Poštovani,						molimo Vas da klikom na sljedeći link : '. $site_url .'?link=' . $link . ' aktivirate svoj račun.						Ili ručno upišete PIN (kojeg korisnici dobivaju putem SMS poruke) : ' . $key . '						Vaša privremena lozinka je : ' . $pass . '						Vaše korisničko ime je : ' . $uid . '						S poštovanjem, \n			Enzo.hr podrška		';				$headers = "From:" . $from;		mail($email,$subject,$message,$headers);	}}?>